mapper.update:
update T_Chapters set
cardId=#{cardId},
seqNum=#{seqNum},
name=#{name},
description=#{description}
where id=#{id}

mapper.select:
select * from T_Chapters
where isDeleted=false
 and id=#{id} 
 and cardId=#{cardId} 
 and seqNum=#{seqNum} 
 and name=#{name} 
 and description=#{description}
