mapper.update:
update T_Cards set
name=#{name},
description=#{description},
courseware=#{courseware}
where id=#{id}

mapper.select:
select * from T_Cards
where isDeleted=false
 and id=#{id} 
 and name=#{name} 
 and createTime=#{createTime} 
 and description=#{description} 
 and courseware=#{courseware}
